var fs = require('fs');

exports.
    sendFile = function(fileName, res) {
        var stream = fs.createReadStream(fileName);
        
        stream.on('error', function() {
            res.statusCode = 500;
            res.end("Server error");
        });
        
        stream.on('close', function() {
            stream.destroy();
        });
        
        stream.pipe(res);
    };